-- Verification Query
SELECT 
    COUNT(*) as total_sales,
    SUM(total_amount) as total_amount,
    SUM(shipping) as total_shipping,
    MIN(date) as earliest_date,
    MAX(date) as latest_date
FROM sales 
WHERE date >= '2025-06-01' AND date <= '2025-06-30';

-- Sample Results
SELECT 
    s.reference,
    s.date,
    s.invoice_number,
    s.total_amount,
    s.shipping,
    COUNT(si.id) as item_count
FROM sales s
LEFT JOIN sale_items si ON s.id = si.sale_id
WHERE s.date >= '2025-06-01' AND s.date <= '2025-06-30'
GROUP BY s.id, s.reference, s.date, s.invoice_number, s.total_amount, s.shipping
ORDER BY s.date DESC, s.invoice_number DESC
LIMIT 10;
